/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `FunInsertEmployee`(
	v_first_name VARCHAR(50), 	v_middle_name VARCHAR(50), 
	v_last_name VARCHAR(50),	v_gender VARCHAR(1), 
	v_dob DATE, 			v_marital_status VARCHAR(10), 
	v_blood_group VARCHAR(6), 	v_qualification VARCHAR(50), 
	v_doj DATE, 			v_username VARCHAR(50), 
	v_password VARCHAR(200), 	v_photo VARCHAR(255), 
	v_dept_id INT, 			v_job_id INT, 
	v_address VARCHAR(200), 	v_city VARCHAR(200),
	v_state VARCHAR(100), 		v_zip_code VARCHAR(50),
	v_per_email VARCHAR(100), 	v_off_email VARCHAR(100),
	v_mobile_no VARCHAR(15),	v_landline_no VARCHAR(15)
	
	) RETURNS varchar(200) CHARSET utf8
BEGIN
    
    DECLARE dup_result INT DEFAULT -1;
    DECLARE v_emp_id INT DEFAULT -1;
    DECLARE v_pl FLOAT DEFAULT 0;
    DECLARE v_day_of_month INT DEFAULT 0;
    DECLARE v_message VARCHAR(200);
    declare v_per int default 0;
	-- IF username already exists, then return error condition.
	
	SELECT COUNT(*) INTO dup_result FROM employee_details WHERE username = v_username;
	
	IF(dup_result > 0)
	THEN
		SET v_message = 'Error: Username already exists.. Please try with some other usernames. Thank you..';
		RETURN v_message;
	END IF;
	
	-- IF personal and official email id already exists, then return error condition.
	
	SELECT COUNT(*) INTO dup_result FROM contact_details WHERE off_email = v_off_email;
	SELECT COUNT(*) INTO v_per FROM contact_details WHERE per_email = v_per_email;
	
	IF(dup_result > 0)
	THEN
		SET v_message = 'Error: A user with same official email id already exists.. Please try with some other official email ids. Thank you..';
		RETURN v_message;
	end if;
		
	IF(v_per > 0)
	THEN
		SET v_message = 'Error: A user with same personal email id already exists.. Please try with some other personal email ids. Thank you..';
		RETURN v_message;
	END IF;
	
	-- IF both are not existing, then insert employee in employee_details table.
	
	INSERT INTO employee_details(
	first_name, middle_name, last_name,
	gender, dob, marital_status, blood_group,
	qualification, doj, username, PASSWORD,
	photo, dept_id, job_id) VALUES(
