    v_delim VARCHAR(1),
    v_project_title VARCHAR(200),
    v_project_client VARCHAR(200),
    v_release_date DATE
    ) RETURNS varchar(200) CHARSET utf8
BEGIN
    
  DECLARE i INT DEFAULT 0;
  DECLARE v_result INT DEFAULT 0;
  DECLARE tok_var VARCHAR(200);
  DECLARE v_message VARCHAR(200);
  DECLARE v_length INT DEFAULT 0;
  DECLARE v_project_id INT DEFAULT 0;
	-- Select project title to check whether it already exists
	SELECT COUNT(*) INTO v_result FROM project_details WHERE project_title = v_project_title;
	
	-- If the project title already exists then return error condition
	IF(v_result > 0)
	THEN
		RETURN 'Error : Project with same title already exists. Please try with some other title for the project. Thank you';
	ELSE
	
	-- Else continue inserting the values in project_details table
	
		INSERT INTO project_details(project_title, project_client, release_date) VALUES(v_project_title, v_project_client, v_release_date);
		
	-- Check whether the insertion is successful or not.
		SELECT COUNT(*) INTO v_result FROM project_details WHERE project_title = v_project_title;
	-- If the values are inserted successfully in the project_detials table, then continue inserting values in the assigned_project_details table
		IF(v_result > 0)
		 THEN
			-- Select the project_id from project_details table
			SELECT project_id INTO v_project_id FROM project_details WHERE project_title = v_project_title;
			
			WHILE i < splitter_count(v_emp_id, v_delim) DO
  
				SELECT(SUBSTRING_INDEX(SUBSTRING_INDEX(v_emp_id, v_delim, i+1), v_delim, -1)) INTO tok_var;
				SELECT COUNT(*) INTO v_result FROM assigned_project_details WHERE project_id = v_project_id AND emp_id = tok_var;
				
				IF(v_result > 0) THEN
				-- The record already exists, then return the error condition.
