				ELSE
				-- Insert the record into the assigned_project_details table.
					INSERT INTO assigned_project_details(project_id, emp_id) VALUES(v_project_id, tok_var);
				END IF;
				SET i = i + 1;
			END WHILE;
			
			-- Count the number of employees ids received as the parameter
			SELECT ((LENGTH(REPLACE(v_emp_id, v_delim, CONCAT(v_delim, ' '))) - LENGTH(v_emp_id))+1) INTO v_length;
			
			-- select the count of records present in the assigned_project_details table.
