/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `InserLeaveCancellationRequest`(
    p_leave_id INT, 
    p_cancellation_id INT,   
    p_action_type INT
    ) RETURNS varchar(200) CHARSET utf8
BEGIN
    DECLARE v_status INT;
    declare v_emp_id int;
    declare v_leave_type varchar(15);
    declare v_no_of_days int;
   
	if(p_action_type = 1) -- If Cancellation is accepted
	then
		SELECT STATUS INTO v_status FROM applied_leave_details WHERE leave_id = p_leave_id;
		IF(v_status = 0)
		THEN
			UPDATE applied_leave_details SET STATUS = 3 WHERE leave_id = p_leave_id;
			UPDATE leave_cancellation_details SET cancellation_status = 4 WHERE cancellation_id = p_cancellation_id;
		ELSE
			UPDATE applied_leave_details SET STATUS = 3 WHERE leave_id = p_leave_id;
			UPDATE leave_cancellation_details SET cancellation_status = 4 WHERE cancellation_id = p_cancellation_id;
			-- Add leaves
			select a.emp_id, a.leave_type, a.no_of_days into v_emp_id, v_leave_type, v_no_of_days from applied_leave_details a where a.leave_id = p_leave_id;
		
			if(v_leave_type = 'PL')
			then
				update available_leave_details set pl = pl + v_no_of_days where emp_id = v_emp_id;
			elseif(v_leave_type = 'CL')
			then
				UPDATE available_leave_details SET cl = cl + v_no_of_days WHERE emp_id = v_emp_id;
			elseif(v_leave_type = 'SL')
			then
				UPDATE available_leave_details SET sl = sl + v_no_of_days WHERE emp_id = v_emp_id;
			elseif(v_leave_type = 'RH')
			THEN
				UPDATE available_leave_details SET rh = rh - v_no_of_days WHERE emp_id = v_emp_id;
			elseIF(v_leave_type = 'LOP')
			THEN
				UPDATE available_leave_details SET lop = lop - v_no_of_days WHERE emp_id = v_emp_id;
			End if;
		END IF;
	else		-- If Cancellation is dismissed
		SELECT al.STATUS INTO v_status FROM applied_leave_details al WHERE al.leave_id = p_leave_id;
		IF(v_status = 0)
		THEN
			UPDATE applied_leave_details SET STATUS = 1 WHERE leave_id = p_leave_id;
			UPDATE leave_cancellation_details SET cancellation_status = 5 WHERE cancellation_id = p_cancellation_id;
			-- Deduct Leaves
			SELECT a.emp_id, a.leave_type, a.no_of_days INTO v_emp_id, v_leave_type, v_no_of_days FROM applied_leave_details a WHERE a.leave_id = p_leave_id;
 		
			IF(v_leave_type = 'PL')
			THEN
				UPDATE available_leave_details SET pl = pl - v_no_of_days WHERE emp_id = v_emp_id;
			ELSEIF(v_leave_type = 'CL')
			THEN
				UPDATE available_leave_details SET cl = cl - v_no_of_days WHERE emp_id = v_emp_id;
			ELSEIF(v_leave_type = 'SL')
			THEN
				UPDATE available_leave_details SET sl = sl - v_no_of_days WHERE emp_id = v_emp_id;
			ELSEIF(v_leave_type = 'RH')
			THEN
				UPDATE available_leave_details SET rh = rh + v_no_of_days WHERE emp_id = v_emp_id;
			ELSEIF(v_leave_type = 'LOP')
			THEN
				UPDATE available_leave_details SET lop = lop + v_no_of_days WHERE emp_id = v_emp_id;
			END IF;         
		ELSE
			UPDATE applied_leave_details SET STATUS = 1 WHERE leave_id = p_leave_id;
			UPDATE leave_cancellation_details SET cancellation_status = 5 WHERE cancellation_id = p_cancellation_id;
		END IF;
	end if;
	return "hi";
    END */$$
