/* Procedure structure for procedure `notifications_not_viewed` */

/*!50003 DROP PROCEDURE IF EXISTS  `notifications_not_viewed` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `notifications_not_viewed`(IN p_time_id INT, IN v_commented_by INT, IN v_comment_description VARCHAR(1000))
BEGIN
	DECLARE v_emp_id INT DEFAULT -1;
	DECLARE p_emp_id INT DEFAULT -1;
	
	SET p_emp_id = v_commented_by;
	
	INSERT INTO comment_details(time_id, commented_by, comment_description, comment_date) 
	VALUES(p_time_id, v_commented_by, v_comment_description, NOW());
	
	-- select who is the owner of the timesheet into v_emp_id	
	SELECT emp_id INTO v_emp_id FROM time_details WHERE time_id = p_time_id;
	-- Check if the owner of the timesheet is commenting.
	IF(v_emp_id = p_emp_id)
	THEN
		-- This means that the commentor is the owner of the timesheet. Now, notification should be sent to all the managers of this employee
		-- To do this, first check whether the entry containing the list of managers and the time_id is present or not.
		-- If present, then update the count_num value.
		-- If not, then insert a new record
		BLOCK1: BEGIN
			-- Find out the list of managers of this employee
			DECLARE v_main INT DEFAULT 0;
			DECLARE m_emp_id INT DEFAULT 0; -- m_emp_id means the employee_id of the manager.
			DECLARE manager_cursor CURSOR FOR SELECT manager_id FROM hierarchy_details WHERE emp_id = p_emp_id;
			DECLARE CONTINUE HANDLER FOR NOT FOUND SET v_main = 1;
			OPEN manager_cursor;
			read_loop: LOOP
				FETCH manager_cursor INTO m_emp_id;
				IF(v_main = 1)
				THEN
					LEAVE read_loop;
				ELSE
					-- Now that the manager's emp_id is in m_emp_id varaible, check if the record already exists.
					IF((SELECT COUNT(*) FROM notification_not_viewed_by WHERE emp_id = m_emp_id AND time_id = p_time_id) > 0)
					THEN
						-- This means that the record is already present. In this case, just increment the count
						UPDATE notification_not_viewed_by SET count_num = count_num + 1 WHERE emp_id = m_emp_id AND time_id = p_time_id;
					ELSE
						-- This means that the record does not exists. I this case, insert a record
						INSERT INTO notification_not_viewed_by(emp_id, time_id, count_num) VALUES(m_emp_id, p_time_id, 1);
					END IF;
				END IF;
			END LOOP read_loop;
			CLOSE manager_cursor;
		END BLOCK1;
	ELSE
    
		-- This means that the commentor is manager. In this case, identify the manager and instead of that manager, insert the owner of the timesheet.
		-- once the manager is identified, skip him
		-- Once inserting all the managers is completed, the owner if the timesheet also meeds to be inserted.
		-- To insert the owner's record, first check whether his record associated with the time_id is present or not.
		-- If present, then update the count_num value
		-- If not present then insert a new row
		BLOCK2: BEGIN
			DECLARE v_main INT DEFAULT 0;
			DECLARE m_emp_id INT DEFAULT 0;
			DECLARE manager_cursor CURSOR FOR SELECT h.manager_id FROM hierarchy_details h, time_details t WHERE t.emp_id = h.emp_id AND t.time_id = p_time_id;
			DECLARE CONTINUE HANDLER FOR NOT FOUND SET v_main = 1;
			OPEN manager_cursor;
			read_loop: LOOP
				FETCH manager_cursor INTO m_emp_id;
				IF(v_main = 1)
				THEN
					LEAVE read_loop;
				END IF;
				-- Skip the manager who is commenting.
				IF(m_emp_id != p_emp_id)
				THEN
					-- Check if the current manager with time_id is already present in the notification table. If yes, then increment the count.
					IF((SELECT COUNT(*) FROM notification_not_viewed_by WHERE emp_id = m_emp_id AND time_id = p_time_id ) > 0)
					THEN
						-- Record of this employee already exists. Just increment the count_num
						UPDATE notification_not_viewed_by SET count_num = count_num + 1 WHERE emp_id = m_emp_id AND time_id = p_time_id;
					ELSE
						-- No employee with this time_id is found. Insert this employee.
						INSERT INTO notification_not_viewed_by(emp_id, time_id, count_num) VALUES(m_emp_id, p_time_id, 1);
					END IF;
				END IF;
			END LOOP read_loop;
			-- Check if the owner of the timesheet already exists.
			IF((SELECT COUNT(*) FROM notification_not_viewed_by WHERE emp_id = v_emp_id AND time_id = p_time_id) > 0)
			THEN
				-- Increment the count_num
				UPDATE notification_not_viewed_by SET count_num = count_num + 1 WHERE emp_id = v_emp_id AND time_id = p_time_id;
			ELSE
				-- insert a new record
				INSERT INTO notification_not_viewed_by(emp_id, time_id, count_num) VALUES(v_emp_id, p_time_id, 1);
			END IF;
			CLOSE manager_cursor;
		END BLOCK2;
	END IF;
END */$$
DELIMITER ;

