DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `ProAddNewEmployee`(in v_first_name varchar(50),in v_middle_name varchar(50), in v_last_name varchar(50), 
    in v_gender varchar(1), in v_dob date, in v_marital_state varchar(10), in v_blood_group varchar(6), in v_qualification varchar(50), in v_doj date, 
    in v_username varchar(50), in v_password varchar(200), in v_photo varchar(255), in v_dept_id int, in v_job_id int,
    in v_address varchar(50), in v_city varchar(50), in v_state varchar(50), in v_zip_code varchar(50), in v_per_email varchar(50), 
    in v_off_email varchar(50), in v_mobile_no varchar(15), in v_landline_no varchar(15), in v_pl int, in v_cl int, in v_sl int, in v_lop int)
BEGIN
    
    declare var_emp_id int;
    
    insert into employee_details(
    first_name, middle_name, last_name, gender, dob, marital_status, blood_group, 
    qualification, doj, username, password, photo,job_id, dept_id)
    values(
    v_first_name, v_middle_name, v_last_name, v_gender, v_dob, v_marital_status, v_blood_group,
    v_qualification, v_doj, v_username, v_password, v_photo, v_job_id, v_dept_id
    );
    
    select emp_id into var_emp_id from employee_details where username = v_username and off_email = v_off_email;
    
    insert into contact_details(
    emp_id, address, city, state, zip_code, per_email, off_email, mobile_no, landline_no)
    values(
    var_emp_id, v_address, v_city, v_state, v_zip_code, v_per_email, v_off_email, v_mobile_no, v_landline_no);
    
    insert into available_leave_details(
    emp_id, pl, cl, sl, lop)
    values(var_emp_id, v_pl, v_cl, v_sl, v_lop);
    
    END */$$
DELIMITER ;

/* Procedure structure for procedure `ProcessCompoff` */

/*!50003 DROP PROCEDURE IF EXISTS  `ProcessCompoff` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `ProcessCompoff`( IN v_compoff_id INT, IN v_emp_id INT, IN v_no_of_days FLOAT, IN v_reason VARCHAR(1000), IN v_status_applied INT)
BEGIN
    
	COMMIT;
	SET autocommit = off;
	
	UPDATE compoff_details SET status_applied = v_status_applied, reason = v_reason, no_of_days = v_no_of_days WHERE compoff_id = v_compoff_id;
	
	IF(v_status_applied = 1)
	THEN
		UPDATE available_leave_details SET compoff = compoff + v_no_of_days WHERE emp_id = v_emp_id;
	END IF;
	
	COMMIT;
	SET autocommit = ON;
	
    END */$$
DELIMITER ;

/* Procedure structure for procedure `ProStarMonth` */

/*!50003 DROP PROCEDURE IF EXISTS  `ProStarMonth` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `ProStarMonth`()
BEGIN
	DECLARE mon INT;
	SELECT MONTH(CURRENT_DATE) INTO mon;
	IF(mon = 1)
	THEN
		SELECT s.emp_id, e.first_name, e.last_name, s.date_star, s.description FROM employee_details e, 
		star_details s WHERE s.emp_id = e.emp_id AND MONTH(s.date_star) = 12 AND 
		YEAR(s.date_star) = (YEAR(NOW()) - 1);
	ELSE
		SELECT s.emp_id, e.first_name, e.last_name, s.date_star, s.description FROM employee_details e, 
		star_details s WHERE s.emp_id = e.emp_id AND MONTH(s.date_star) = (MONTH(NOW()) - 1) AND 
		YEAR(s.date_star) = YEAR(NOW());
	END IF;
    END */$$
DELIMITER ;

