/* Procedure structure for procedure `subCompoff` */

/*!50003 DROP PROCEDURE IF EXISTS  `subCompoff` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `subCompoff`( IN p_emp_id INT, IN p_no_of_days FLOAT)
BEGIN
    
    DECLARE v_emp_id INT DEFAULT 0;
    DECLARE v_no_of_days FLOAT DEFAULT 0;
    DECLARE v_compoff_id INT DEFAULT 0;
    DECLARE sub_compoff CURSOR FOR SELECT compoff_id, no_of_days FROM compoff_details WHERE DAYOFYEAR(CURRENT_DATE) - DAYOFYEAR(date_applied) < 90 AND emp_id = p_emp_id AND status_applied = 1 ORDER BY date_worked ASC;
    
    OPEN sub_compoff;
    
    read_loop: LOOP
    
	FETCH sub_compoff INTO v_compoff_id, v_no_of_days;
	
	IF(p_no_of_days != 0)
	THEN
    
		IF(v_no_of_days = p_no_of_days)
		THEN
	
			UPDATE compoff_details SET status_applied = 4, no_of_days = 0 WHERE compoff_id = v_compoff_id;
			
			SET p_no_of_days = 0;
		
			LEAVE read_loop;
		
		ELSEIF(v_no_of_days > p_no_of_days)
		THEN
	
			UPDATE compoff_details SET no_of_days = v_no_of_days - p_no_of_days WHERE compoff_id = v_compoff_id;
		
			SET p_no_of_days = 0;
		
			LEAVE read_loop;
		
		ELSEIF(v_no_of_days < p_no_of_days)
		THEN
	
			UPDATE compoff_details SET no_of_days = 0, status_applied = 4 WHERE compoff_id = v_compoff_id;
			
			SET p_no_of_days = p_no_of_days - v_no_of_days;
		 
		END IF;
		
	END IF;
    END LOOP;
    		
    CLOSE sub_compoff;
    
    END */$$
DELIMITER ;

/* Procedure structure for procedure `UpdateCompoff` */

/*!50003 DROP PROCEDURE IF EXISTS  `UpdateCompoff` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `UpdateCompoff`()
BEGIN
    
   DECLARE v_compoff_id INT DEFAULT 0;
   DECLARE v_emp_id INT DEFAULT 0;
   DECLARE v_no_of_days float DEFAULT 0;
   DECLARE compoff_cur CURSOR FOR SELECT compoff_id, emp_id, no_of_days FROM compoff_details WHERE DATEDIFF(CURRENT_DATE, date_worked) >= 90 AND status_applied = 1;
	OPEN compoff_cur;
	
		read_loop:LOOP
	
			FETCH compoff_cur INTO v_compoff_id, v_emp_id, v_no_of_days;
		
			UPDATE available_leave_details SET compoff = compoff - v_no_of_days WHERE emp_id = v_emp_id;
		
			UPDATE compoff_details SET status_applied = 3 WHERE compoff_id = v_compoff_id;
		
		END LOOP;
		
	CLOSE compoff_cur;
	
	update compoff_details set status_applied = 2 where DATEDIFF(CURRENT_DATE, date_worked) >= 90 and status_applied = 0;
    END */$$
DELIMITER ;

